CREATE TABLE IF NOT EXISTS users (id INT NOT NULL AUTO_INCREMENT PRIMARY KEY, name VARCHAR(255) NOT NULL , email VARCHAR(255) NOT NULL , salt VARCHAR(255) NOT NULL , password TEXT NOT NULL);
CREATE TABLE IF NOT EXISTS code_reviews (id INT NOT NULL AUTO_INCREMENT PRIMARY KEY, input_code LONGTEXT NOT NULL, output_code LONGTEXT NULL, user_id INT NOT NULL, FOREIGN KEY(user_id) REFERENCES users(id) ON DELETE CASCADE);